export const SimpleHand = {
    setup: () => ({
        total: 0,
        hand: Array(7).fill(1),        
    }),

    turn: {
        moveLimit: 1,
    },

    moves:{
        clickCard: (G,ctx, id) => {
            G.total += G.hand[id];
        },
    },
};
