import React from 'react';
import SimpleCard from './SimpleCard.js'

export class SimpleHandBoard extends React.Component {
    renderSimpleCard(name,description) {
        return <SimpleCard name={name} description = {description} />;
    }
    onClick(id) {
        this.props.moves.clickCard(id);
    }

    render(G)
    {
        let cardHand = [];
        for(let i = 0; i < this.props.G.hand.length; i++)
        {
            cardHand.push(
                <div onClick={() => this.onClick(this.props.G.hand[i])} >
                    {this.renderSimpleCard(this.props.G.hand[i], "This is a sample description.")}
                </div>
                );
        }

        let winner = '';
        if (this.props.ctx.gameover) {
            winner =
        this.props.ctx.gameover.winner !== undefined ? (
          <div id="winner">Winner: {this.props.ctx.gameover.winner}</div>
        ) : (
          <div id="winner">Draw!</div>
        );
    }


        return (
            <div style = {{textAlign: "center", marginRight: '300px'}}>
                <p>{this.props.G.total}</p>
                
                <div style = {{display: 'flex', flexDirection: 'row', justifyContent: 'center', marginRight: '300px'}}>
                    {cardHand}
                </div>
                {winner}
                
            </div>
            
        );
    }
    
}
