import logo from './logo.svg';
import './App.css';
import React from 'react';
import SimpleCard from './SimpleCard.js'

class App extends React.Component{
  renderSimpleCard(name,description) {
    return <SimpleCard name={name} description = {description} />;
  }

  render(){
    return(
          <div className="App">
            <header className="App-header">
              <img src={logo} className="App-logo" alt="logo" />
              <a
                className="App-link"
                href="https://reactjs.org"
                target="_blank"
                rel="noopener noreferrer"
              >
                Learn React
              </a>
              <div style={{display: 'flex', flexDirection: 'row'}}>
                {this.renderSimpleCard("This is the Title","This is the description.")}
                {this.renderSimpleCard("Second Card", "This is the second description.")}
                {this.renderSimpleCard("Third Card", "This is the third description.")}
                {this.renderSimpleCard("Fourth Card", "Test Test Test Test Test Test Test Test Test Test. Test Test Test Test Test Test Test Test Test Test. Test Test Test Test Test Test Test Test Test Test.Test Test Test Test Test Test Test Test Test Test.")}

              </div>
            </header> 
          </div>
    );
  }
}


export default App;
