import logo from './logo.svg';
import './App.css';
import React from 'react';
import Card from 'react-bootstrap/Card';


class SimpleCard extends React.Component{
  render (props) {
    const cellStyle = {
      border: '1px solid #555',
      width: '100px',
      height: '200px',
      lineHeight: '30px',
      textAlign: 'center',
      fontSize: 12,
    };
    return(
      <Card style = {cellStyle}>
        <Card.Title>
          {this.props.name}
        </Card.Title>
        <Card.Body>
          {this.props.description}
        </Card.Body>
      </Card>
    );
  }
}


class App extends React.Component{
  renderSimpleCard(name,description) {
    return <SimpleCard name={name} description = {description} />;
  }

  render(){
    return(
          <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <div style={{display: 'flex', flexDirection: 'row'}}>
          {this.renderSimpleCard("Try Again","This is the description")}
          {this.renderSimpleCard("Second Card", "Second Description")}
          {this.renderSimpleCard("Second Card", "Second Description")}
          {this.renderSimpleCard("Second Card", "Second Description")}
          {this.renderSimpleCard("Second Card", "Second Description")}
        </div>
      </header>
    </div>
    );
  }
}


export default App;
